const chalk = require('chalk');
const boxen = require('boxen');
const cliBoxes = require('cli-boxes');

const data = {
  name: chalk.white('Jeyssonclay Godoy de Souza |'),
  handle: chalk.cyan('JeyGodoy'),
  skills: chalk.cyan('QA Analyst / Cypress | JavaScript / NodeJS | SQL / NoSQL | Java | Low-code / Mendix, OutSystems'),
  github: chalk.cyan('https://github.com/jeygodoy'),
  linkedin: chalk.cyan('https://www.linkedin.com/in/jey-godoy/'),
  email: chalk.cyan('jeygodoy.ads@gmail.com'),
  softSkills: chalk.cyan('Adaptability + Emotional Intelligence + Communication + Creativity + Self-management'),
  npx: chalk.cyan('npx cardjeygodoy'),
  labels: {
    skills: chalk.white.bold('Dev in:'),
    github: chalk.white.bold('GitHub:'),
    linkedin: chalk.white.bold('LinkedIn:'),
    email: chalk.white.bold('Email:'),
    softSkills: chalk.white.bold('Soft Skills:'),
    card: chalk.white.bold('This Card:'),
  },
};

const box = cliBoxes.single;

const output = [
  `${data.name} ${data.handle}`,
  '',
  `${data.labels.skills} ${data.skills}`,
  `${data.labels.softSkills}\n${data.softSkills}`,
  `${data.labels.github} ${data.github}`,
  `${data.labels.linkedin} ${data.linkedin}`,
  `${data.labels.email} ${data.email}`,
  '',
  `${data.labels.card} ${data.npx}`,
].join('\n');

const boxedOutput = boxen(output, { ...box });

console.log(chalk.green(boxedOutput));
